@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .

atm:Bank
    rdf:type owl:Class ;
    rdfs:subClassOf [
        rdf:type owl:Restriction ;
        owl:onProperty atm:hasLimit ;
        owl:someValuesFrom [
            rdf:type owl:Class ;
            owl:intersectionOf (
                atm:Limit
                [
                    rdf:type owl:Restriction ;
                    owl:onProperty atm:forAccount ;
                    owl:someValuesFrom atm:Account
                ]
                [
                    rdf:type owl:Restriction ;
                    owl:onProperty atm:forCashCard ;
                    owl:someValuesFrom atm:CashCard
                ]
            )
        ]
    ] .

atm:Limit
    rdf:type owl:Class ;
    rdfs:subClassOf [
        rdf:type owl:Restriction ;
        owl:onProperty atm:hasAmount ;
        owl:someValuesFrom xsd:decimal
    ] .

atm:hasLimit rdf:type owl:ObjectProperty .
atm:forAccount rdf:type owl:ObjectProperty .
atm:forCashCard rdf:type owl:ObjectProperty .
atm:hasAmount rdf:type owl:DatatypeProperty .
