@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

atm:ATM a owl:Class .
atm:Message a owl:Class .

atm:sendsMessage a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:Message .

atm:transactionFailed a atm:Message .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix atm:  <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If a condition is not met, the ATM sends a "transaction failed" message.
atm:TransactionFailedShape a sh:NodeShape ;
  sh:targetClass atm:ATM ;
  sh:sparql [
    sh:message "If a condition is not met, the ATM sends a 'transaction failed' message." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:ATM .
        FILTER NOT EXISTS { ?this atm:sendsMessage atm:transactionFailed . }
      }
    """
  ] .
