@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:ATM a owl:Class .
atm:BankComputer a owl:Class .
atm:CashCard a owl:Class .
atm:DisplayState a owl:Class .

atm:hasCashCard a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  atm:CashCard .

atm:communicatesWith a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  atm:BankComputer .

atm:displaysState a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  atm:DisplayState .

atm:responseTime a owl:DatatypeProperty ;
  rdfs:domain atm:BankComputer ;
  rdfs:range  xsd:duration .

atm:cardRejected a owl:DatatypeProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  xsd:boolean .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
atm:ErrorDisplay a atm:DisplayState .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix atm:  <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

# If responseTime > 2 minutes, then cardRejected MUST be true and ErrorDisplay is shown
atm:SlowResponseRejectsCardShape a sh:NodeShape ;
  sh:targetClass atm:ATM ;
  sh:sparql [
    sh:message "If responseTime > 2 minutes, ATM must reject card and display ErrorDisplay." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:ATM ; atm:communicatesWith/atm:responseTime ?rt .
        FILTER (?rt > "PT2M"^^xsd:duration)
        FILTER NOT EXISTS { ?this atm:cardRejected ?cr . FILTER (?cr = true) }
        FILTER NOT EXISTS { ?this atm:displaysState atm:ErrorDisplay }
      }
    """
  ] .
