@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:Transaction a owl:Class .
atm:Message a owl:Class .
atm:Bank a owl:Class .
atm:ATM a owl:Class .

atm:transactionSucceeded a owl:DatatypeProperty ;
  rdfs:domain atm:Transaction ;
  rdfs:range xsd:boolean .

atm:sendsMessage a owl:ObjectProperty ;
  rdfs:domain atm:Bank ;
  rdfs:range atm:Message .

atm:receivesMessage a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:Message .

atm:messageContent a owl:DatatypeProperty ;
  rdfs:domain atm:Message ;
  rdfs:range xsd:string .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
atm:TransactionSucceededMessage a atm:Message ;
  atm:messageContent "transaction succeeded" .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

# If transactionSucceeded == true, then the bank must send TransactionSucceededMessage to the ATM.
atm:SuccessSendsMessageShape a sh:NodeShape ;
  sh:targetClass atm:Transaction ;
  sh:sparql [
    sh:message "If transaction succeeded, bank must send 'transaction succeeded' message to ATM." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:Transaction ; atm:transactionSucceeded ?ts .
        FILTER (?ts = true)
        FILTER NOT EXISTS {
          ?bank atm:sendsMessage atm:TransactionSucceededMessage .
          ?atm atm:receivesMessage atm:TransactionSucceededMessage .
        }
      }
    """
  ] .
