@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .

:Bank rdf:type owl:Class .
:Computer rdf:type owl:Class .
:Software rdf:type owl:Class .
:Security rdf:type owl:Class .

:providesSecurity rdf:type owl:ObjectProperty ;
    rdfs:domain :Bank ;
    rdfs:range :Security .

:owns rdf:type owl:ObjectProperty ;
    rdfs:domain :Bank ;
    rdfs:range [ rdf:type owl:Class ;
                 owl:unionOf ( :Computer :Software ) ] .

:Security rdfs:subClassOf [ rdf:type owl:Restriction ;
                            owl:onProperty :providesSecurity ;
                            owl:someValuesFrom :Bank ] .

:Bank rdfs:subClassOf [ rdf:type owl:Restriction ;
                         owl:onProperty :owns ;
                         owl:someValuesFrom [ rdf:type owl:Class ;
                                              owl:unionOf ( :Computer :Software ) ] ] .
