@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:ATM a owl:Class .
:CashCard a owl:Class .
:Account a owl:Class .
:BankComputer a owl:Class .
:Message a owl:Class .

:hasCashCard a owl:ObjectProperty ;
  rdfs:domain :ATM ;
  rdfs:range :CashCard .

:hasAccount a owl:ObjectProperty ;
  rdfs:domain :ATM ;
  rdfs:range :Account .

:sendsMessage a owl:ObjectProperty ;
  rdfs:domain :BankComputer ;
  rdfs:range :Message .

:validCashCard a owl:DatatypeProperty ;
  rdfs:domain :CashCard ;
  rdfs:range xsd:boolean .

:validPassword a owl:DatatypeProperty ;
  rdfs:domain :CashCard ;
  rdfs:range xsd:boolean .

:validAccount a owl:DatatypeProperty ;
  rdfs:domain :Account ;
  rdfs:range xsd:boolean .

:messageContent a owl:DatatypeProperty ;
  rdfs:domain :Message ;
  rdfs:range xsd:string .
@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
:AccountOkMessage a :Message ;
  :messageContent "account ok" .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If the cash card, password, and account are valid, the bank computer will send a message to the ATM indicating that everything is okay.
:ValidCardPasswordAccountShape a sh:NodeShape ;
  sh:targetClass :ATM ;
  sh:sparql [
    sh:message "If the cash card, password, and account are valid, the bank computer will send a message to the ATM indicating that everything is okay." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a :ATM ; :hasCashCard ?card ; :hasAccount ?account .
        ?card :validCashCard ?vcc ; :validPassword ?vp .
        ?account :validAccount ?va .
        FILTER (?vcc = true && ?vp = true && ?va = true)
        FILTER NOT EXISTS { 
          ?bc a :BankComputer ; :sendsMessage :AccountOkMessage .
        }
      }
    """
  ] .
