@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .

atm:Transaction a owl:Class ;
    rdfs:subClassOf [
        a owl:Restriction ;
        owl:onProperty atm:hasRequestedAmount ;
        owl:someValuesFrom [
            a owl:Class ;
            owl:intersectionOf (
                [ a owl:Restriction ; owl:onProperty atm:exceedsLimit ; owl:hasValue true ]
            )
        ]
    ] .

atm:hasRequestedAmount a owl:ObjectProperty .

atm:exceedsLimit a owl:DatatypeProperty ;
    rdfs:range xsd:boolean .

atm:TransactionFailure a owl:Class ;
    rdfs:subClassOf atm:Transaction ;
    rdfs:subClassOf [
        a owl:Restriction ;
        owl:onProperty atm:hasRequestedAmount ;
        owl:someValuesFrom [
            a owl:Class ;
            owl:intersectionOf (
                [ a owl:Restriction ; owl:onProperty atm:exceedsLimit ; owl:hasValue true ]
            )
        ]
    ] .
