@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:Transaction a owl:Class .
atm:Limit a owl:Class .

atm:hasLimit a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:Limit .

atm:requestedAmount a owl:DatatypeProperty ;
  rdfs:domain atm:Transaction ;
  rdfs:range xsd:decimal .

atm:limitValue a owl:DatatypeProperty ;
  rdfs:domain atm:Limit ;
  rdfs:range xsd:decimal .

atm:transactionStatus a owl:DatatypeProperty ;
  rdfs:domain atm:Transaction ;
  rdfs:range xsd:string .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

# If the requested amount exceeds the limit, the transaction will fail.
atm:ExceedsLimitFailsShape a sh:NodeShape ;
  sh:targetClass atm:Transaction ;
  sh:sparql [
    sh:message "If the requested amount exceeds the limit, the transaction will fail." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:Transaction ; atm:requestedAmount ?reqAmount .
        ?atm atm:hasLimit/atm:limitValue ?limit .
        FILTER (?reqAmount > ?limit)
        FILTER NOT EXISTS { ?this atm:transactionStatus ?status . FILTER (?status = "failed") }
      }
    """
  ] .
