@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ATM a owl:Class .
:Bank a owl:Class .
:Message a owl:Class .

:sendsMessage a owl:ObjectProperty ;
  rdfs:domain :Bank ;
  rdfs:range :Message .

:receivesMessage a owl:ObjectProperty ;
  rdfs:domain :ATM ;
  rdfs:range :Message .

:BadAccount a :Message .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix :  <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If a bank sends a message, the ATM must receive the same message.
:BankSendsATMReceivesShape a sh:NodeShape ;
  sh:targetClass :Bank ;
  sh:sparql [
    sh:message "If a bank sends a message, the ATM must receive the same message." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a :Bank ; :sendsMessage ?msg .
        FILTER NOT EXISTS { ?atm a :ATM ; :receivesMessage ?msg . }
      }
    """
  ] .

# If a bank sends "BadAccount" message, the ATM must receive "BadAccount" message.
:BankSendsBadAccountShape a sh:NodeShape ;
  sh:targetClass :Bank ;
  sh:sparql [
    sh:message "If a bank sends 'BadAccount' message, the ATM must receive 'BadAccount' message." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a :Bank ; :sendsMessage :BadAccount .
        FILTER NOT EXISTS { ?atm a :ATM ; :receivesMessage :BadAccount . }
      }
    """
  ] .
