@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix : <http://www.example.org#> .

:Platform rdf:type owl:Class .

:Item rdf:type owl:Class .

:Inventory rdf:type owl:Class .

:restock rdf:type owl:ObjectProperty ;
         rdfs:domain :Platform ;
         rdfs:range :Item .

:hasInventory rdf:type owl:ObjectProperty ;
              rdfs:domain :Platform ;
              rdfs:range :Inventory .

:hasInStock rdf:type owl:DatatypeProperty ;
            rdfs:domain :Item ;
            rdfs:range xsd:boolean .

[] rdf:type owl:Class ;
   owl:equivalentClass [ rdf:type owl:Restriction ;
                         owl:onProperty :hasInventory ;
                         owl:someValuesFrom [ rdf:type owl:Restriction ;
                                              owl:onProperty :hasInStock ;
                                              owl:hasValue false^^xsd:boolean
                                            ]
                       ] ;
   rdfs:subClassOf [ rdf:type owl:Restriction ;
                     owl:onProperty :restock ;
                     owl:someValuesFrom :Item
                   ] .

:replenished rdf:type owl:ObjectProperty ;
             rdfs:domain :Inventory ;
             rdfs:range :Item .

[] rdf:type owl:Class ;
   owl:equivalentClass [ rdf:type owl:Restriction ;
                         owl:onProperty :replenished ;
                         owl:someValuesFrom :Item
                       ] ;
   rdfs:subClassOf [ rdf:type owl:Restriction ;
                     owl:onProperty :restock ;
                     owl:someValuesFrom :Item
                   ] .
