@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix : <http://www.example.com/ontology#> .

:Storefront a owl:Class ;
    rdfs:comment "The storefront of a website or platform" .

:BroadbandConnection a owl:Class ;
    rdfs:comment "Standard broadband connection used to access a website or platform" .

:loadsInUnderTwoSeconds a owl:ObjectProperty ;
    rdfs:domain :Storefront ;
    rdfs:range :BroadbandConnection ;
    rdfs:comment "The property indicates that the storefront must load in under two seconds on a standard broadband connection" .

:StandardBroadband a :BroadbandConnection ;
    rdfs:comment "The standard broadband connection" .

[] a owl:Axiom ;
    owl:propertyChainAxiom (:Storefront :loadsInUnderTwoSeconds :BroadbandConnection) ;
    owl:inverseOf :loadsInUnderTwoSeconds ;
    rdfs:comment "Axiom stating that the storefront must load in under two seconds on a standard broadband connection" .

:Storefront :loadsInUnderTwoSeconds :StandardBroadband .
