@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

:BankComputer a owl:Class .
:InvalidBankCode a owl:Class .

:sendsMessage a owl:ObjectProperty ;
  rdfs:domain :BankComputer ;
  rdfs:range :Message .

:hasBankCode a owl:DatatypeProperty ;
  rdfs:domain :CashCard ;
  rdfs:range xsd:string .

:isValid a owl:DatatypeProperty ;
  rdfs:domain :CashCard ;
  rdfs:range xsd:boolean .
@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
:BadBankCodeMessage a :Message ;
  :messageCode "bad bank code" .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If the bank code is invalid, the bank computer sends the message 'bad bank code' to the ATM.
:InvalidBankCodeShape a sh:NodeShape ;
  sh:targetClass :CashCard ;
  sh:sparql [
    sh:message "If the bank code is invalid, the bank computer sends the message 'bad bank code' to the ATM." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a :CashCard ; :isValid ?valid .
        FILTER (?valid = false)
        FILTER NOT EXISTS { 
          ?computer a :BankComputer ; :sendsMessage :BadBankCodeMessage .
        }
      }
    """
  ] .
