@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

atm:ATM a owl:Class .
atm:BankComputer a owl:Class .
atm:Message a owl:Class .
atm:Password a owl:Class .

atm:hasPassword a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:Password .

atm:sendsMessage a owl:ObjectProperty ;
  rdfs:domain atm:BankComputer ;
  rdfs:range atm:Message .

atm:indicatesBadPassword a owl:ObjectProperty ;
  rdfs:domain atm:Message ;
  rdfs:range atm:Password .

atm:valid a owl:DatatypeProperty ;
  rdfs:domain atm:Password ;
  rdfs:range xsd:boolean .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
atm:BadPasswordMessage a atm:Message .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If the password is invalid, the bank computer shall send a message to the ATM indicating a bad password.
atm:InvalidPasswordTriggersMessageShape a sh:NodeShape ;
  sh:targetClass atm:ATM ;
  sh:sparql [
    sh:message "If the password is invalid, the bank computer must send a message indicating a bad password." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:ATM ; atm:hasPassword ?pw .
        ?pw atm:valid ?valid .
        FILTER (?valid = false)
        FILTER NOT EXISTS {
          ?bc a atm:BankComputer ; atm:sendsMessage ?msg .
          ?msg atm:indicatesBadPassword ?pw .
        }
      }
    """
  ] .
