@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:ATM a owl:Class .
atm:ErrorMessage a owl:Class .

atm:displaysMessage a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:ErrorMessage .

atm:displayDuration a owl:DatatypeProperty ;
  rdfs:domain atm:ErrorMessage ;
  rdfs:range xsd:integer .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

# If an error message is displayed, it must be displayed for at least 30 seconds.
atm:ErrorMessageDurationShape a sh:NodeShape ;
  sh:targetClass atm:ErrorMessage ;
  sh:property [
    sh:path atm:displayDuration ;
    sh:datatype xsd:integer ;
    sh:minInclusive 30 ;
    sh:message "Error message must be displayed for at least 30 seconds."
  ] .
