@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:ATM a owl:Class .
atm:ErrorMessage a owl:Class .

atm:hasErrorMessage a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range atm:ErrorMessage .

atm:displayTime a owl:DatatypeProperty ;
  rdfs:domain atm:ErrorMessage ;
  rdfs:range xsd:integer .

# Axiom: Error message display time should be at least 30 seconds
[ a owl:Restriction ;
  owl:onProperty atm:displayTime ;
  owl:minInclusive 30
] rdfs:subClassOf atm:ErrorMessage .
