@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

atm:Transaction a owl:Class .
atm:CashCard a owl:Class .
atm:DisplayState a owl:Class .

atm:isSuccessful a owl:DatatypeProperty ;
  rdfs:domain atm:Transaction ;
  rdfs:range  xsd:boolean .

atm:displaysState a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  atm:DisplayState .

atm:ejectsCard a owl:ObjectProperty ;
  rdfs:domain atm:ATM ;
  rdfs:range  atm:CashCard .
@prefix atm: <http://lod.csd.auth.gr/atm/atm.ttl#> .
atm:ErrorMessage a atm:DisplayState .
atm:CardEjected a atm:CashCard .
@prefix sh:  <http://www.w3.org/ns/shacl#> .
@prefix atm:  <http://lod.csd.auth.gr/atm/atm.ttl#> .

# If the transaction is not successful, an error message should be displayed
atm:UnsuccessfulTransactionShowsErrorShape a sh:NodeShape ;
  sh:targetClass atm:Transaction ;
  sh:sparql [
    sh:message "If the transaction is not successful, an error message should be displayed." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:Transaction ; atm:isSuccessful ?success .
        FILTER (?success = false)
        FILTER NOT EXISTS { ?atm atm:displaysState atm:ErrorMessage }
      }
    """
  ] .

# If the transaction is not successful, the card should be ejected
atm:UnsuccessfulTransactionEjectsCardShape a sh:NodeShape ;
  sh:targetClass atm:Transaction ;
  sh:sparql [
    sh:message "If the transaction is not successful, the card should be ejected." ;
    sh:select """
      SELECT ?this WHERE {
        ?this a atm:Transaction ; atm:isSuccessful ?success .
        FILTER (?success = false)
        FILTER NOT EXISTS { ?atm atm:ejectsCard atm:CardEjected }
      }
    """
  ] .
