@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix : <http://www.example.com/ontology#> .

:MedicalRecord a owl:Class ;
    rdfs:comment "A class represents medical records in the system." .

:ArchiveRecord a owl:Class ;
    rdfs:comment "A class represents archived medical records that are older than seven years." .

:hasArchiveDate a owl:DatatypeProperty ;
    rdfs:domain :MedicalRecord ; 
    rdfs:range xsd:dateTime .

:hasArchiveRecords a owl:ObjectProperty ;
    rdfs:domain :MedicalRecord ;
    rdfs:range :ArchiveRecord .

:ArchiveSevenYearsRule a owl:Class ;
    owl:equivalentClass [
        a owl:Restriction ;
        owl:onProperty :hasArchiveDate ;
        owl:someValuesFrom [
            a rdfs:Datatype ;
            owl:onDatatype xsd:dateTime ;
            owl:withRestrictions ( [ xsd:minInclusive "P7Y"^^xsd:duration ] ) ;
        ] ;
    ] ;
    rdfs:subClassOf :MedicalRecord ;
    owl:intersectionOf ( 
        :MedicalRecord 
        [
            a owl:Restriction ;
            owl:onProperty :hasArchiveRecords ;
            owl:someValuesFrom :ArchiveRecord ;
        ]
    ) ;
    rdfs:comment "A class represents the rule that medical records older than seven years must be archived." .
