@prefix : <http://lod.csd.auth.gr/atm/atm.ttl#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:Bank a owl:Class .
:Computer a owl:Class .
:Software a owl:Class .
:Security a owl:Class .

:providesSecurity a owl:ObjectProperty ;
  rdfs:domain :Bank ;
  rdfs:range :Security .

:hasComputer a owl:ObjectProperty ;
  rdfs:domain :Bank ;
  rdfs:range :Computer .

:hasSoftware a owl:ObjectProperty ;
  rdfs:domain :Bank ;
  rdfs:range :Software .

:protects a owl:ObjectProperty ;
  rdfs:domain :Security ;
  rdfs:range owl:Class .

# Axiom: The bank only provides security for their own computer and their own software.
[ a owl:Class ;
  owl:equivalentClass 
    [ a owl:Restriction ;
      owl:onProperty :providesSecurity ;
      owl:someValuesFrom 
        [ a owl:Class ;
          owl:intersectionOf 
            ( :Security 
              [ a owl:Restriction ;
                owl:onProperty :protects ;
                owl:someValuesFrom 
                  [ a owl:Class ;
                    owl:unionOf 
                      ( [ a owl:Restriction ;
                          owl:onProperty :hasComputer ;
                          owl:someValuesFrom :Computer ]
                        [ a owl:Restriction ;
                          owl:onProperty :hasSoftware ;
                          owl:someValuesFrom :Software ] ) ] ] ) ] ] ] .
