competency_questions.query:
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
ASK {
  FILTER NOT EXISTS {
    VALUES ?cls {
      atm:ATM atm:Bank atm:Customer atm:Account atm:CashCard
      atm:Transaction atm:Withdrawal atm:Deposit atm:Authorization
    }
    FILTER NOT EXISTS { ?cls a owl:Class . }
  }
}

# Withdrawals specialise the transaction concept (directly or indirectly)
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:Withdrawal rdfs:subClassOf+ atm:Transaction .
}

# Deposits are also treated as transactions
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:Deposit rdfs:subClassOf+ atm:Transaction .
}

# ATMs connect either to transactions (gold) or to cash cards (operational)
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:handlesTransaction rdfs:domain atm:ATM ;
                           rdfs:range atm:Transaction .
  } AS ?handlesTransactions)
  BIND(EXISTS {
    atm:accepts rdfs:domain atm:ATM ;
                rdfs:range atm:CashCard .
  } AS ?acceptsCards)
  FILTER (?handlesTransactions || ?acceptsCards)
}

# ATMs are associated with banks either via operations or ownership
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:operatedBy rdfs:domain atm:ATM ;
                   rdfs:range atm:Bank .
  } AS ?operatedBy)
  BIND(EXISTS {
    atm:belongsToBank rdfs:domain atm:ATM ;
                      rdfs:range [ owl:onProperty atm:belongsToBank ;
                                   owl:allValuesFrom atm:Bank
                                 ] .
  } AS ?belongsTo)
  FILTER (?operatedBy || ?belongsTo)
}

# Banks maintain customer relationships
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:maintainsAccount rdfs:domain atm:Bank ;
                        rdfs:range atm:Account .
  } AS ?maintainsAccount)
  BIND(EXISTS {
    atm:hasCustomer rdfs:domain atm:Bank .
  } AS ?hasCustomers)
  FILTER (?maintainsAccount || ?hasCustomers)
}

# Customers are linked to cash cards in both ontologies
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:ownsCard rdfs:domain atm:Customer ;
                 rdfs:range atm:CashCard .
  } AS ?ownsCard)
  BIND(EXISTS {
    atm:hasCashCard rdfs:domain atm:Customer ;
                    rdfs:range atm:CashCard .
  } AS ?hasCashCard)
  FILTER (?ownsCard || ?hasCashCard)
}

# Customers or their accounts are linked together
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:hasAccount rdfs:domain atm:Customer ;
                  rdfs:range atm:Account .
  } AS ?customerAccounts)
  BIND(EXISTS {
    atm:isHold rdfs:domain atm:Account .
  } AS ?accountHeldBy)
  FILTER (?customerAccounts || ?accountHeldBy)
}

# Transaction amounts are numeric in both versions
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
ASK {
  BIND(EXISTS {
    atm:requestedAmount rdfs:domain atm:Transaction ;
                      rdfs:range xsd:decimal .
  } AS ?requestedAmount)
  BIND(EXISTS {
    atm:transactionAmount rdfs:range xsd:integer .
  } AS ?transactionAmount)
  FILTER (?requestedAmount || ?transactionAmount)
}

# Withdrawal-specific information is present
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
ASK {
  BIND(EXISTS {
    atm:dispensedAmount rdfs:domain atm:Withdrawal ;
                        rdfs:range xsd:decimal .
  } AS ?dispensedAmount)
  BIND(EXISTS {
    atm:transactionAmount rdfs:domain ?domain .
    ?domain owl:unionOf/rdf:rest*/rdf:first ?member .
    FILTER (?member = atm:ATMTransaction || ?member = atm:Transaction)
  } AS ?transactionAmountDomain)
  FILTER (?dispensedAmount || ?transactionAmountDomain)
}

# ATMs explicitly dispense cash
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:dispenses rdfs:domain atm:ATM ;
                rdfs:range atm:Money .
}

# ATM user interfaces capture both normal and error displays
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:displays rdfs:domain atm:ATM ;
               rdfs:range ?displayUnion .
  ?displayUnion owl:unionOf ?list .
  FILTER EXISTS { ?list rdf:rest*/rdf:first atm:NormalDisplay }
  FILTER EXISTS { ?list rdf:rest*/rdf:first atm:ErrorDisplay }
}

# Resilience features include backup or main power supplies
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:hasBackUpPowerSupply rdfs:domain atm:ATM ;
                             rdfs:range atm:PowerSupply .
  } AS ?backup)
  BIND(EXISTS {
    atm:hasMainPowerSupply rdfs:domain atm:ATM ;
                           rdfs:range atm:PowerSupply .
  } AS ?main)
  FILTER (?backup || ?main)
}

# Operational timings are captured for security-critical checks
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:hasCardVerificationTime rdfs:domain atm:ATM ;
                                 rdfs:range atm:Time .
  } AS ?cardVerification)
  BIND(EXISTS {
    atm:hasPinVerificationTime rdfs:domain atm:ATM ;
                                rdfs:range atm:Time .
  } AS ?pinVerification)
  FILTER (?cardVerification && ?pinVerification)
}

# Bidirectional communication with the bank back-end is represented
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:fromBankComputer rdfs:range atm:BankComputer .
  } AS ?fromBankComputer)
  BIND(EXISTS {
    atm:toBankComputer rdfs:range atm:BankComputer .
  } AS ?toBankComputer)
  BIND(EXISTS {
    atm:toATM rdfs:range atm:ATM .
  } AS ?toAtm)
  FILTER (?fromBankComputer && ?toBankComputer && ?toAtm)
}

# ATM and bank systems update transactional metrics
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:updates rdfs:range ?rangeNode .
  ?rangeNode owl:unionOf ?union .
  FILTER EXISTS { ?union rdf:rest*/rdf:first atm:ATMtotalFundOfATM }
  FILTER EXISTS { ?union rdf:rest*/rdf:first atm:AccountBalanceOFAccount }
}

# Requirements modelling captures subject, verb, condition and else branches
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  BIND(EXISTS {
    atm:subject rdfs:domain atm:BasicRequirement ;
                rdfs:range atm:Subject .
  } AS ?subject)
  BIND(EXISTS {
    atm:verb rdfs:domain atm:BasicRequirement ;
             rdfs:range atm:Verb .
  } AS ?verb)
  BIND(EXISTS {
    atm:condition rdfs:domain atm:ComplexRequirement ;
                  rdfs:range atm:BasicRequirement .
  } AS ?condition)
  BIND(EXISTS {
    atm:else rdfs:domain atm:ComplexRequirement ;
             rdfs:range atm:BasicRequirement .
  } AS ?else)
  FILTER (?subject && ?verb && ?condition && ?else)
}

# Customers interacting with ATMs use both passwords and transaction amounts
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:types rdfs:domain atm:Customer ;
            rdfs:range ?rangeNode .
  ?rangeNode owl:unionOf ?union .
  FILTER EXISTS { ?union rdf:rest*/rdf:first atm:PasswordOfCustomer }
  FILTER EXISTS { ?union rdf:rest*/rdf:first atm:TransactionAmountOfTransaction }
}

# ATM keypads are defined with explicit key components
PREFIX atm: <http://lod.csd.auth.gr/atm/atm.ttl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
ASK {
  atm:consistOfKeys rdfs:domain atm:Keypad ;
                    rdfs:range atm:Keys .
}

